# 通过openpyxl与time库修改绩效表中日期为当前时间
def write_excel():
    # 读取excel
    wb = openpyxl.load_workbook('jixiaokaohe.xlsx')
    worksheet = wb['sheet名称']

    # 修改指定行数据并保存到本地
    worksheet.cell(2, 8, str(time.strftime('%Y.%m.%d', time.localtime())))
    wb.save(filename='jixiaokaohe.xlsx')
# 发送邮件
def send_email():

    from_addr = 'zpli@gizwits.com'
    password = 'Gwli33833110'
    # to_addr = ",".join(["mzfe<1017476271@qq.com>", "mrtin<louis-me@hotmail.com>"])
    to_addr = ",".join(["mzfe<1017476271@qq.com>"])
    smtp_server = 'smtp.exmail.qq.com'
    smtp_port = 465

    # 创建一个带附件的实例
    msg = MIMEMultipart()
    # msg['From'] = Header("李兆鹏Alan", 'utf-8')
    msg['From'] = "Alan<zpli@gizwits.com>"
    msg['To'] = to_addr
    # msg['To'] = ",".join(["昵称1"<nichengyi@163.com>,"昵称2"<nichengwer@qq.com>])
    subject = '图片'
    msg['Subject'] = Header(subject, 'utf-8')

    # 正文
    msg.attach(MIMEText("你好:<p>详情见附件！</p> <img src='data:image/png;base64,'/>", 'html', 'utf-8'))

    # 附件1
    att1 = MIMEText(open('log.word', 'rb').read(), 'base64', 'utf-8')
    # 这里的filename可以任意写，写什么名字，邮件中显示什么名字
    att1["Content-Disposition"] = 'attachment; filename="log.word"'
    msg.attach(att1)

    # 构造附件2
    # att2 = MIMEText(open('d:\\123.txt', 'rb').read(), 'base64', 'gb2312')
    # att2["Content-Type"] = 'application/octet-stream'
    # att2["Content-Disposition"] = 'attachment; filename="123.txt"'
    # msg.attach(att2)

    # 连接smtp服务器，明文/SSL/TLS三种方式，根据你使用的SMTP支持情况选择一种
    # 普通方式，通信过程不加密
    # smtp = smtplib.SMTP(smtp_server, 25)
    # smtp.ehlo()
    # smtp.login(from_addr, password)

    # 纯粹的ssl加密方式，通信过程加密，邮件数据安全
    server = smtplib.SMTP_SSL(smtp_server, smtp_port)
    server.ehlo()
    # server.set_debuglevel(true)
    server.login(from_addr, password)

    try:
        server.sendmail(from_addr, [to_addr], msg.as_string())  # 发送地址需与登陆的邮箱一致
        print("邮件发送成功！")
        server.quit()
    except smtplib.SMTPException:
        print("Error: 无法发送邮件")

def start():
    send_email()

